import React, {Component} from "react";
import {MDBCard, MDBCardBody, MDBCardImage, MDBCardText, MDBCardTitle, MDBCol, MDBRow} from "mdbreact";
import {findallWorkshop} from "../../services/workshop.service";

export class Attendee extends Component {


    constructor(props) {
        super(props);
        this.state = {
            workshopList: [],
            workshopListStatus: false
        }
        this.getAllWorkshopList = this.getAllWorkshopList.bind(this);
        this.getAllWorkshopList();
    }

    getAllWorkshopList() {
        findallWorkshop().then(res => {
            console.log(res.data)
            this.setState({
                workshopList: res.data,
                workshopListStatus: true
            })
        }).catch(error => console.error(error));
    }


    render() {
        return (
            <MDBRow className="justify-content-center">
                <MDBCol md="6" lg="9">
                    <section className="text-center pb-3">
                        <MDBRow className="d-flex justify-content-center">
                            {
                                this.state.workshopListStatus ?
                                    this.state.workshopList.map(wsc => {
                                        console.log('ppppppppppp'+wsc.date)
                                        return (
                                            <div className="col-sm-4 cardMarginTop">
                                                <MDBCard className="d-flex mb-5">
                                                    <MDBCardBody>
                                                        <MDBCardTitle className="font-bold mb-3">
                                                            <strong>{wsc.workShop}</strong>
                                                        </MDBCardTitle>
                                                        <MDBCardText>Some quick example text to build on the card
                                                            title
                                                            and make up the bulk of the card's
                                                            content.</MDBCardText>
                                                        <span className="h5 text-right mb-4">Duration</span> :<span
                                                        style={{fontWeight: "bold"}}>{wsc.date} Days</span>
                                                        <br/>
                                                        <span className="h5 text-left mb-4">Start Date</span> :<span
                                                        style={{fontWeight: "bold"}}>{wsc.time}</span>
                                                        <br/>
                                                        <span className="h5 text-left mb-4">End  Date</span> :<span
                                                        style={{fontWeight: "bold"}}> {wsc.fees}</span>
                                                        <br/>
                                                        <span
                                                            className="h5 text-left mb-4">Cost(One Person)</span>: <span
                                                        style={{fontWeight: "bold"}}>RS:{wsc.conductor}</span>
                                                        <br/>
                                                        <span className="h5 text-left mb-4">Transport</span> :<span
                                                        style={{fontWeight: "bold"}}>{wsc.email}</span>
                                                        <br/>
                                                        <span className="h5 text-left mb-4">Person Count </span> :<span
                                                        style={{fontWeight: "bold"}}>{wsc.contact}</span>
                                                        <br/>
                                                        <MDBBtn outline color="info" size="sm" onClick={this.toggle2}>
                                                            Sign Up</MDBBtn>
                                                    </MDBCardBody>
                                                </MDBCard>
                                            </div>
                                        )
                                    })
                                    : ''
                            }
                        </MDBRow>
                    </section>
                </MDBCol>
            </MDBRow>
        );
    }

}
