import React, {Component} from "react";
import {MDBAlert, MDBBtn, MDBCard, MDBCardBody, MDBModalBody, MDBRow} from "mdbreact";
import {getUser, saveUser} from "../../services/signup.service";
import Swal from "sweetalert2";

export default class UserRemove extends Component {

    constructor(props) {
        super(props);
        this.state = {
            collapse: false,
            isWideEnough: false,
            modal: false,
            model2: false,
            editfname: '',
            editemail: '',
            email: '',
            editpassword: '',
            editconfirmpass: '',
            loginEmail: '',
            loginPass: '',
            editfirstName: '',
            loginEmailV: false,
            loginPassV: false,
            UserList: {},
            UserListStatus: false,
        };

        this.onClick = this.onClick.bind(this);
        this.SearchUser = this.SearchUser.bind(this);
        this.onChangeFname = this.onChangeFname.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onChangeConfirmPassword = this.onChangeConfirmPassword.bind(this);
        this.onChangeEmailV = this.onChangeEmailV.bind(this);
        this.onChangePassV = this.onChangePassV.bind(this);
        this.onChangeUserType = this.onChangeUserType.bind(this);
    }

    onClick() {
        this.setState({
            collapse: !this.state.collapse,
        });
    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal
        });
    }

    toggle2 = () => {
        this.setState({
            modal: !this.state.modal,
            modal2: !this.state.model2
        });
    }

    onChangeEmailV(event) {
        this.setState({
            loginEmail: event.target.value,
            [event.target.name]: event.target.value
        })
    }

    onChangePassV(event) {
        this.setState({
            loginPass: event.target.value,
            [event.target.name]: event.target.value
        })
    }

    onChangeFname(event) {
        console.log(event.target.value)
        this.setState({
            editfname: event.target.value,
            [event.target.name]: event.target.value
        })
    }

    onChangeEmail(event) {
        this.setState({
            editemail: event.target.value,
            [event.target.name]: event.target.value
        })
    }

    onChangePassword(event) {
        this.setState({
            editpassword: event.target.value,
            [event.target.name]: event.target.value
        })
    }

    onChangeConfirmPassword(event) {
        this.setState({
            editconfirmpass: event.target.value,
            [event.target.name]: event.target.value
        })
    }

    onChangeUserType(event) {
        this.setState({
            edituserType: event.target.value,
            [event.target.name]: event.target.value
        })
    }

    SearchUser(event) {
        event.preventDefault();
        event.target.className += " was-validated";
        if (this.state.email != '') {
            const userDetail = {
                email: this.state.email,
            }
            console.log(userDetail);

            getUser(this.state.email).then(res => {
                if (res.data.message === 'not found') {
                    Swal.fire(
                        '',
                        'User Notfound !',
                        'error'
                    )
                } else {
                    console.log('llllllllllllll'+res.data.message.userType);
                    this.setState({
                        UserList: res.data,
                        UserListStatus: true
                    })
                }
            }).catch(error => console.error(error));
        } else {
        }
    };

    SaveEditUser(){

    }

