import React, {Component} from 'react';
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBModalBody,
    MDBRow,
} from "mdbreact";
import "react-datepicker/dist/react-datepicker.css";
import {findallBookedtours} from "../../services/tour.book.service";
import {getUser, saveUser} from "../../services/signup.service";
import Swal from "sweetalert2";
import DateTimePicker from "react-datetime-picker/src/DateTimePicker";

export class WKC extends Component {

    constructor(props) {
        super(props);
        this.state = {
            tourList: [],
            tourListStatus: false,
            fname: '',
            email: '',
            workShopTitle:'',
            datevalue: '',

        };
        this.getAllMyTours = this.getAllMyTours.bind(this);
        this.getAllMyTours();

        this.onClick = this.onClick.bind(this);
        this.submitUser = this.submitUser.bind(this);
        this.onChangeFname = this.onChangeFname.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
    }

    getAllMyTours() {
        findallBookedtours(localStorage.getItem("CustomerId")).then(res => {
            console.log(res.data)
            this.setState({
                tourList: res.data,
                tourListStatus: true
            })
        }).catch(error => console.error(error));
    }

    submitWorkshop(event) {
        event.preventDefault();
        event.target.className += " was-validated";
        if (this.state.workshop != '') {
            if (this.state.fname != '') {
                if (this.state.email != '') {
                    if (this.state.password != '') {
                        if (this.state.confirmpass != '') {
                            const userDetail = {
                                userName: this.state.fname,
                                email: this.state.email,
                                password: this.state.password,
                                userType: this.state.userType
                            }
                            console.log(userDetail);

                            getUser(this.state.email).then(res => {
                                if(res.data.message === 'not found'){
                                    saveUser(userDetail).then(res=>{
                                        if (res.data.success === 'successful') {
                                            this.setState({
                                                fname: '',
                                                password: '',
                                                confirmpass: '',
                                                email: '',
                                                modal2 :false
                                            })
                                            Swal.fire(
                                                '',
                                                'Added Success',
                                                'success'
                                            )
                                        } else {
                                            Swal.fire(
                                                '',
                                                'Added fail',
                                                'error'
                                            )
                                        }
                                    }).catch(error => console.error(error));
                                }else {
                                    Swal.fire(
                                        '',
                                        'You Cant Use This Email Please Enter Another One',
                                        'error'
                                    )
                                }
                            }).catch(error => console.error(error));
                        } else {
                        }
                    } else {
                    }

                } else {
                }

            } else {
            }
        } else {
        }
    };


    render() {
        return (
            <MDBModalBody>
                <MDBCard>
                    <MDBCardBody className="mx-2">
                        <div className="text-center">
                            <h2 className="workshoph2 mb-1">
                                <strong className="workshoptitle">Workshop</strong>
                            </h2>
                        </div>
                        <form className="needs-validation" onSubmit={this.submitWorkshop} noValidate>
                            <MDBRow>
                                <label htmlFor="workshopid" className="grey-text">Workshop Title</label>
                                <input value={this.workShopTitle}
                                       name="workshop"
                                       onChange={this.onChangeWorkShopTitle}
                                       type="text" id="workshopid" className="form-control"
                                       placeholder="Workshop Title" required/>
                                <label htmlFor="datetimeid" className="grey-text">Date Time</label>
                                <DateTimePicker
                                    onChange={this.onChangeDate}
                                    value={this.datevalue}
                                    id="datetimeid"
                                />
                                <label htmlFor="workshopid" className="grey-text">Workshop Title</label>
                                <input value={this.workShopTitle}
                                       name="workshop"
                                       onChange={this.onChangeWorkShopTitle}
                                       type="text" id="workshopid" className="form-control"
                                       placeholder="Workshop Title" required/>
                                <label htmlFor="firstnameid" className="grey-text">First name</label>
                                <input value={this.firstName}
                                       name="fname"
                                       onChange={this.onChangeFname}
                                       type="text" id="firstnameid" className="form-control"
                                       placeholder="First name" required/>
                                <label htmlFor="workshopid" className="grey-text">Workshop Title</label>
                                <input value={this.workShopTitle}
                                       name="workshop"
                                       onChange={this.onChangeWorkShopTitle}
                                       type="text" id="workshopid" className="form-control"
                                       placeholder="Workshop Title" required/>
                                <div className="invalid-feedback">Please provide the first name.</div>
                                <label htmlFor="emailid" className="grey-text">Email</label>
                                <input value={this.email}
                                       onChange={this.onChangeEmail}
                                       type="email"
                                       id="emailid" className="form-control"
                                       name="email"
                                       placeholder="Your Email address" required/>
                                <div className="invalid-feedback">Please provide an email.</div>
                            </MDBRow>

                            <br/>

                            <br></br>
                            <MDBBtn gradient="blue" rounded className="btn-block z-depth-1a" type="submit">
                                Workshop Add
                            </MDBBtn>
                        </form>
                    </MDBCardBody>
                </MDBCard>
            </MDBModalBody>
        );
    }

}
